mapper.update:
UPDATE bookdb.t_book


        book_count = #{bookCount} ,


        bookName = #{bookName} ,


        price = #{price}


        bookImage = #{bookImage}


        author = #{author}


        saleCount = #{saleCount}


        bookStatus = #{bookStatus}



    id = #{id}

mapper.select:
SELECT id, book_count, book_name, price, book_img, author, sale_count, book_status
FROM bookdb.t_book
WHERE id = #{id}

SELECT price
FROM bookdb.t_book
WHERE id = #{id}
